mapper.select:
SELECT m1.seq AS memberSeq, m1.id AS memberID, m1.nickname AS memberNickname, IFNULL(m1.slot_rate, 0) AS memberSlotRate, IFNULL(m1.baccarat_rate, 0) AS memberBaccaratRate, IFNULL(m1.slot_rate, 0) AS slotRate, IFNULL(m1.baccarat_rate, 0) AS baccaratRate, m2.seq AS storeSeq, m2.id AS storeID, IFNULL(m2.slot_rate, 0) AS storeSlotRate, IFNULL(m2.baccarat_rate, 0) AS storeBaccaratRate, m3.seq AS distributorSeq, m3.id AS distributorID, IFNULL(m3.slot_rate, 0) AS distributorSlotRate, IFNULL(m3.baccarat_rate, 0) AS distributorBaccaratRate, m4.seq AS subHeadquarterSeq, m4.id AS subHeadquarterID, IFNULL(m4.slot_rate, 0) AS subHeadquarterSlotRate, IFNULL(m4.baccarat_rate, 0) AS subHeadquarterBaccaratRate, bs.* FROM betting_summary bs LEFT JOIN member m1 ON m1.seq = bs.member_seq LEFT JOIN member m2 ON m2.seq = bs.store_seq LEFT JOIN member m3 ON m3.seq = bs.distributor_seq LEFT JOIN member m4 ON m4.seq = bs.headquarter_seq
AND m1.user_type = #{entity.userType}
AND DATE(bs.check_time) >= DATE(#{entity.fromProcessTime})
AND DATE(#{entity.toProcessTime}) >= DATE(bs.check_time)
AND m2.seq = #{entity.storeSeq}
AND m1.${entity.searchField} LIKE CONCAT('%', #{entity.searchValue}, '%')
AND bs.member_seq = #{entity.memberSeq}
ORDER BY bs.check_time DESC
