SELECT m.seq AS seq, m.sub_headquarter_seq AS headquarterSeq, ( SELECT m1.id FROM member m1 WHERE m1.user_type = #{userTypeSubHeadquarter} AND m1.seq = m.sub_headquarter_seq) AS subHeadquarter, m.id AS id, m.nickname AS nickname, IFNULL(m.money_amount, 0) AS moneyAmount, ( SELECT count(*) FROM member m1 WHERE m1.user_type = #{userTypeStore} AND m1.distributor_seq = m.seq ) AS storeCount, ( SELECT count(*) FROM member m1 WHERE m1.user_type = #{userTypeNormal} AND m1.distributor_seq = m.seq ) AS memberCount, ( SELECT count(*) FROM member m1 LEFT JOIN money_history mh ON m1.seq = mh.receiver WHERE mh.operation_type = #{moneyHistoryOperationTypeDeposit} AND m1.distributor_seq = m.seq AND m1.user_type = #{userTypeNormal} AND mh.money_or_point = #{distributor.isMoney}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
)AS depositMemberCount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.receiver = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeDeposit} AND m1.distributor_seq = m.seq AND m1.user_type = #{userTypeNormal} AND mh.money_or_point = #{distributor.isMoney} AND mh.status = #{moneyHistoryStatusComplete}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS depositMemberAmount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.receiver = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeDeposit} AND ( m1.distributor_seq = m.seq OR m1.seq = m.seq ) AND (m1.user_type = #{userTypeSubHeadquarter} OR m1.user_type = #{userTypeDistributor} OR m1.user_type = #{userTypeStore}) AND mh.money_or_point = #{distributor.isMoney} AND mh.status = #{moneyHistoryStatusComplete}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS depositPartnerAmount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.payer = m1.seq LEFT JOIN member m2 ON mh.receiver = m2.seq AND m2.distributor_seq = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeTransferIn} AND m1.seq = m.seq AND m1.user_type = #{userTypeDistributor} AND mh.money_or_point = #{distributor.isMoney} AND m2.user_type = #{userTypeNormal}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS depositPayment, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.receiver = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeWithdrawal} AND m1.distributor_seq = m.seq AND m1.user_type = #{userTypeNormal} AND mh.money_or_point = #{distributor.isMoney} AND mh.status = #{moneyHistoryStatusComplete}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS withdrawalMemberAmount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.receiver = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeWithdrawal} AND ( m1.distributor_seq = m.seq OR m1.seq = m.seq ) AND (m1.user_type = #{userTypeSubHeadquarter} OR m1.user_type = #{userTypeDistributor} OR m1.user_type = #{userTypeStore}) AND mh.money_or_point = #{distributor.isMoney} AND mh.status = #{moneyHistoryStatusComplete}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS withdrawalPartnerAmount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m1 ON mh.receiver = m1.seq LEFT JOIN member m2 ON mh.payer = m2.seq AND m2.distributor_seq = m1.seq WHERE mh.operation_type = #{moneyHistoryOperationTypeTransferIn} AND m1.seq = m.seq AND m1.user_type = #{userTypeDistributor} AND mh.money_or_point = #{distributor.isMoney} AND m2.user_type = #{userTypeNormal}
AND DATE(mh.application_time) >= DATE(#{distributor.fromApplicationTime})
AND DATE(#{distributor.toApplicationTime}) >= DATE(mh.application_time)
), 0) AS withdrawalPayment, IFNULL(m.slot_rate, 0) AS slotRate, IFNULL(m.baccarat_rate, 0) AS baccaratRate, m.status AS status, IFNULL(bs.betting_amount, 0) AS betAmount, IFNULL(bs.winning_amount, 0) AS winningAmount, IFNULL(bs.lost_amount, 0) AS lostAmount, IFNULL(bs.bat_rolling, 0) AS batRolling, bs.type AS gameType FROM member m LEFT JOIN betting_summary bs ON bs.member_seq = m.seq
GROUP BY m.seq

