SELECT m.nickname
FROM
	member m
LEFT JOIN member_site ms ON m.seq = ms.member_seq
WHERE
	m.level_seq = #{levelSeq}
	AND m.user_type = #{userType}
	AND ms.site_seq = #{siteSeq}

SELECT m1.seq, m1.id, m1.password, m1.nickname, m1.color, m1.name, m1.user_type, IFNULL(m1.money_amount, 0) AS moneyAmount, IFNULL(m1.mileage_amount, 0) AS mileageAmount, m1.bank_name, m1.account_holder, m1.bank_number, m1.exchange_password, m1.register_date, m1.signup_ip, m1.site_domain, m1.site_name, m1.status, m1.email_address, m1.phone_number, IFNULL(m1.member_revenue, 0) AS memberRevenue, m1.recommender, m1.recommended_reason, m1.betting_possible, m1.recommendable, m1.memo, m1.slot_rate, m1.baccarat_rate, m2.seq AS StoreSeq, m2.id AS StoreId, m3.seq AS DistributorSeq, m3.id AS DistributorId, m4.seq AS subHeadquarterSeq, m4.id AS subHeadquarterId, IFNULL(dh.deposit, 0) AS deposit, IFNULL(amh.admin_deposit, 0) AS adminDeposit, IFNULL(wh.withdrawal, 0) AS withdrawal, IFNULL(dh.depositCount, 0) AS depositCount, IFNULL(wh.withdrawalCount, 0) AS withdrawalCount, al.connection_date AS recentDate, al.connection_ip, al.connection_domain AS recentDomain, SUM(IFNULL(bs.betting_amount, 0)) AS betAmount, SUM(IFNULL(bs.winning_amount, 0)) AS winningAmount, SUM(IFNULL(bs.lost_amount, 0)) AS lostAmount, IFNULL(bs.bat_rolling, 0) AS batRolling, level.seq AS levelSeq, level.level_name AS levelName, ( SELECT count(*) FROM member m WHERE m.user_type = #{entity.userTypeDistributor} AND m.sub_headquarter_seq = m1.seq ) AS distributorCount, ( SELECT count(*) FROM member m WHERE m.user_type = #{entity.userTypeStore} AND m.sub_headquarter_seq = m1.seq ) AS storeCount, ( SELECT count(*) FROM member m WHERE m.user_type = #{entity.userTypeMember} AND m.sub_headquarter_seq = m1.seq ) AS memberCount, ( SELECT count(*) FROM member m LEFT JOIN money_history mh ON m.seq = mh.receiver WHERE mh.operation_type = #{entity.operationTypeDeposit} AND m1.sub_headquarter_seq = m.seq AND m1.user_type = #{entity.userTypeMember} AND mh.money_or_point = #{entity.isMoney}) AS depositMemberCount, IFNULL(( SELECT sum( mh.variable_amount ) FROM money_history mh LEFT JOIN member m ON mh.receiver = m.seq WHERE mh.operation_type = #{entity.operationTypeDeposit} AND m.sub_headquarter_seq = m1.seq AND m.user_type = #{entity.userTypeMember} AND mh.money_or_point = #{entity.isMoney}), 0) AS depositMemberAmount FROM member m1 LEFT JOIN LEVEL ON level.seq = m1.level_seq LEFT JOIN member m2 ON m1.store_seq = m2.seq LEFT JOIN member m3 ON m1.distributor_seq = m3.seq LEFT JOIN member m4 ON m1.sub_headquarter_seq = m4.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS deposit, COUNT(mh.seq) AS depositCount, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE (mh.operation_type = #{entity.operationTypeDeposit} OR mh.operation_type = #{entity.operationTypeTransferIn}) AND mh.money_or_point = #{entity.isMoney} AND (mh.status != ${MONEY_HISTORY_STATUS_PARTNER_PAYMENT} AND mh.status = ${MONEY_HISTORY_STATUS_COMPLETE}) GROUP BY mh.receiver) AS dh ON dh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS withdrawal, COUNT(mh.seq) AS withdrawalCount, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE (mh.operation_type = #{entity.operationTypeWithdrawal} OR mh.operation_type = #{entity.operationTypeTransferOut}) AND mh.money_or_point = #{entity.isMoney} AND (mh.status != ${MONEY_HISTORY_STATUS_PARTNER_PAYMENT} AND mh.status = ${MONEY_HISTORY_STATUS_COMPLETE}) GROUP BY mh.receiver) AS wh ON wh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS admin_deposit, mh.receiver FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = ${MONEY_HISTORY_OPERATION_TYPE_DEPOSIT} AND mh.status = ${MONEY_HISTORY_STATUS_PARTNER_PAYMENT} GROUP BY mh.receiver) as amh ON amh.receiver = m1.seq LEFT JOIN (SELECT al.member_seq, al.site, MAX(al.connection_date) as connection_date, al.connection_ip, al.connection_domain FROM access_log AS al LEFT JOIN member mm ON mm.seq = al.member_seq GROUP BY al.member_seq) AS al ON al.member_seq = m1.seq LEFT JOIN betting_summary bs ON bs.member_seq = m1.seq
m1.seq = #{entity.seq}
GROUP BY m1.seq

