AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.receiver) AS dh ON dh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS withdrawal, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = #{entity.operationTypeWithdrawal} AND mh.money_or_point = #{entity.isMoney}
AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.receiver) AS wh ON wh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS admin_deposit, mh.receiver FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = ${MONEY_HISTORY_OPERATION_TYPE_DEPOSIT} AND mh.status = ${MONEY_HISTORY_STATUS_PARTNER_PAYMENT} GROUP BY mh.receiver) as amh ON amh.receiver = m1.seq LEFT JOIN (SELECT al.member_seq, al.site, MAX(al.connection_date) as connection_date, al.connection_ip, al.connection_domain FROM access_log AS al LEFT JOIN member mm ON mm.seq = al.member_seq GROUP BY al.member_seq) AS al ON al.member_seq = m1.seq LEFT JOIN betting_summary bs ON bs.member_seq = m1.seq
AND m1.user_type = #{entity.userType}
AND m1.sub_headquarter_seq = #{entity.subHeadquarterSeq}
AND m1.distributor_seq = #{entity.distributorSeq}
AND m1.store_seq = #{entity.storeSeq}
GROUP BY m1.seq ORDER BY m1.create_date DESC

