SELECT m1.seq, m1.id, m1.name, m1.nickname, IFNULL(m1.money_amount, 0) AS moneyAmount, IFNULL(m1.mileage_amount, 0) AS mileageAmount, m1.user_type AS userType, level.seq AS levelSeq, level.level_name AS levelName, IFNULL(bs.betting_amount, 0) AS betAmount, IFNULL(bs.winning_amount, 0) AS winningAmount, IFNULL(bs.lost_amount, 0) AS lostAmount, IFNULL(bs.bat_rolling, 0) AS batRolling, bs.type AS gameType, m1.status, m1.register_date, m1.site_domain, m1.recommender, m2.seq AS StoreSeq, m2.id AS StoreId, m2.nickname AS StoreNickname, m3.seq AS DistributorSeq, m3.id AS DistributorId, m3.nickname AS distributorNickname, m4.seq AS subHeadquarterSeq, m4.id AS subHeadquarterId, m4.nickname AS subHeadquarterNickname, m1.recommendable, IFNULL(dh.deposit, 0) AS deposit, IFNULL(amh.admin_deposit, 0) AS adminDeposit, IFNULL(wh.withdrawal, 0) AS withdrawal, IFNULL(ph.payment, 0) AS payment, IFNULL(ch.collect, 0) AS collect, al.connection_date, al.connection_ip, al.connection_domain FROM member m1 LEFT JOIN LEVEL ON level.seq = m1.level_seq LEFT JOIN member m2 ON m1.store_seq = m2.seq LEFT JOIN member m3 ON m1.distributor_seq = m3.seq LEFT JOIN member m4 ON m1.sub_headquarter_seq = m4.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS deposit, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = #{entity.operationTypeDeposit} AND mh.money_or_point = #{entity.isMoney}
AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.receiver) AS dh ON dh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS withdrawal, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = #{entity.operationTypeWithdrawal} AND mh.money_or_point = #{entity.isMoney}
AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.receiver) AS wh ON wh.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS payment, mh.receiver AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver LEFT JOIN member m5 ON m5.seq = mh.payer AND (m.sub_headquarter_seq = m5.seq OR m.distributor_seq = m5.seq OR m.store_seq = m5.seq) WHERE mh.operation_type = #{MONEY_HISTORY_OPERATION_TYPE_TRANSFER_IN} AND mh.money_or_point = #{entity.isMoney}
AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.receiver) AS ph ON ph.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS collect, mh.payer AS seq FROM money_history mh LEFT JOIN member m ON m.seq = mh.payer LEFT JOIN member m5 ON m5.seq = mh.receiver AND (m.sub_headquarter_seq = m5.seq OR m.distributor_seq = m5.seq OR m.store_seq = m5.seq) WHERE mh.operation_type = #{MONEY_HISTORY_OPERATION_TYPE_TRANSFER_OUT} AND mh.money_or_point = #{entity.isMoney}
AND DATE(mh.application_time) >= DATE(#{entity.fromApplicationTime})
AND DATE(#{entity.toApplicationTime}) >= DATE(mh.application_time)
GROUP BY mh.payer) AS ch ON ch.seq = m1.seq LEFT JOIN (SELECT SUM(mh.variable_amount) AS admin_deposit, mh.receiver FROM money_history mh LEFT JOIN member m ON m.seq = mh.receiver WHERE mh.operation_type = ${MONEY_HISTORY_OPERATION_TYPE_DEPOSIT} AND mh.status = ${MONEY_HISTORY_STATUS_PARTNER_PAYMENT} GROUP BY mh.receiver) as amh ON amh.receiver = m1.seq LEFT JOIN (SELECT al.member_seq, al.site, MAX(al.connection_date) as connection_date, al.connection_ip, al.connection_domain FROM access_log AS al LEFT JOIN member mm ON mm.seq = al.member_seq GROUP BY al.member_seq) AS al ON al.member_seq = m1.seq LEFT JOIN betting_summary bs ON bs.member_seq = m1.seq
m1.user_type = #{entity.userType}
AND (m1.sub_headquarter_seq = #{entity.subHeadquarterSeq} OR m1.distributor_seq = #{entity.subHeadquarterSeq} OR m1.store_seq = #{entity.subHeadquarterSeq})
AND m1.site_name = #{entity.siteName}
AND m1.id = #{entity.id}
AND m1.${entity.searchField} LIKE CONCAT('%', #{entity.searchValue}, '%')
AND m1.status IN
#{item}
AND m1.level_seq IN
#{item}
AND m1.sub_headquarter_seq = #{entity.subHeadquarterSeq}
AND m1.distributor_seq = #{entity.distributorSeq}
AND m1.store_seq = #{entity.storeSeq}
GROUP BY m1.seq ORDER BY m1.${column}
DESC
ASC
